package com.class03;

public class RecapJava {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a=10;
		int b=20;
		String x="Hello";
		String y="Bye";
		//ctrl+space - syso 
		
		System.out.println(a+b+x+y);
		System.out.println(x+y+a+b);
		System.out.println(a+""+b+x+y);
	}

}
